    private void jToggleButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jToggleButton1ActionPerformed
        
        int anoAtual = Integer.parseInt(jTextFieldEntradaAnoAtaul.getText());
        int anoNascimento = Integer.parseInt(jTextFieldEntradaAnoNascimento.getText());
        
        
        jLabelFinalMeses.setText(String.valueOf((anoAtual - anoNascimento) * 12));
        jLabelFinalAnos.setText(String.valueOf(anoAtual - anoNascimento));
        jLabelFinalDias.setText(String.valueOf((anoAtual - anoNascimento) * 365));
        
        
        
        
    }//GEN-LAST:event_jToggleButton1ActionPerformed

